jQuery.fn.MytoJson = function(options) {

    options = jQuery.extend({}, options);

    var self = this,
        json = {},
        push_counters = {},
        patterns = {
            "validate": /^[a-zA-Z][a-zA-Z0-9_]*(?:\[(?:\d*|[a-zA-Z0-9_]+)\])*$/,
            "key":      /[a-zA-Z0-9_]+|(?=\[\])/g,
            "push":     /^$/,
            "fixed":    /^\d+$/,
            "named":    /^[a-zA-Z0-9_]+$/
        };


    this.build = function(base, key, value){
        base[key] = value;
        return base;
    };

    this.push_counter = function(key){
        if(push_counters[key] === undefined){
            push_counters[key] = 0;
        }
        return push_counters[key]++;
    };
    
    var inputs = jQuery(this).serializeArray();
//    console.log(jQuery(this).tagName);
//    console.log(jQuery(this).serializeArray());
//    console.log(jQuery(this).find('input[type=checkbox]').length);
    jQuery(this).find('input[type=checkbox]').each(
                    function() {
                        if ($(this).attr('checked') == 'checked')
                        {
                            var checkbox = $(this);
                            var present = false;
                            $(inputs).each
                            (
                                function()
                                {
                                    if (this.name == checkbox.attr('name') && this.value == checkbox.val())
                                    {
                                        present = true;
                                        return;
                                    }
                                });
                            if (!present)
                            {
                                inputs.push({'name':$(this).attr('name'),'value':$(this).val()});
                            }
                            
//                            console.log('checked');
                        }
                        
                    });
                    
    console.log(inputs);
    jQuery.each(inputs, function(){

        // skip invalid keys
        // Mis en commentaire pour le getWallPost
//        if(!patterns.validate.test(this.name)){
//            console.log(this.name);
//            return;
//        }

        var k,
            keys = this.name.match(patterns.key),
            merge = this.value,
            reverse_key = this.name;

        while((k = keys.pop()) !== undefined){

            // adjust reverse_key
            reverse_key = reverse_key.replace(new RegExp("\\[" + k + "\\]$"), '');

            // push
            if(k.match(patterns.push)){
                merge = self.build([], self.push_counter(reverse_key), merge);
            }

            // fixed
            else if(k.match(patterns.fixed)){
                merge = self.build([], k, merge);
            }

            // named
            else if(k.match(patterns.named)){
                merge = self.build({}, k, merge);
            }
        }
//        console.log(json);

        json = jQuery.extend(true, json, merge);
    });


    return json;
}

